PRAGMA foreign_keys=OFF;
START TRANSACTION;
CREATE TABLE IF NOT EXISTS "migrations" ("id" integer primary key autoincrement not null, "migration" varchar not null, "batch" integer not null);
CREATE TABLE IF NOT EXISTS "password_reset_tokens" ("email" varchar not null, "token" varchar not null, "created_at" datetime, primary key ("email"));
CREATE TABLE IF NOT EXISTS "failed_jobs" ("id" integer primary key autoincrement not null, "uuid" varchar not null, "connection" text not null, "queue" text not null, "payload" text not null, "exception" text not null, "failed_at" datetime not null default CURRENT_TIMESTAMP);
CREATE TABLE IF NOT EXISTS "personal_access_tokens" ("id" integer primary key autoincrement not null, "tokenable_type" varchar not null, "tokenable_id" integer not null, "name" varchar not null, "token" varchar not null, "abilities" text, "last_used_at" datetime, "expires_at" datetime, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "data_types" ("id" integer primary key autoincrement not null, "name" varchar not null, "slug" varchar not null, "display_name_singular" varchar not null, "display_name_plural" varchar not null, "icon" varchar, "model_name" varchar, "description" varchar, "generate_permissions" tinyint(1) not null default '0', "created_at" datetime, "updated_at" datetime, "server_side" integer not null default '0', "controller" varchar, "policy_name" varchar, "details" text);
CREATE TABLE IF NOT EXISTS "data_rows" ("id" integer primary key autoincrement not null, "data_type_id" integer not null, "field" varchar not null, "type" varchar not null, "display_name" varchar not null, "required" tinyint(1) not null default '0', "browse" tinyint(1) not null default '1', "read" tinyint(1) not null default '1', "edit" tinyint(1) not null default '1', "add" tinyint(1) not null default '1', "delete" tinyint(1) not null default '1', "details" text, "order" integer not null default '1', foreign key("data_type_id") references "data_types"("id") on delete cascade on update cascade);
CREATE TABLE IF NOT EXISTS "menus" ("id" integer primary key autoincrement not null, "name" varchar not null, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "menu_items" ("id" integer primary key autoincrement not null, "menu_id" integer, "title" varchar not null, "url" varchar not null, "target" varchar not null default '_self', "icon_class" varchar, "color" varchar, "parent_id" integer, "order" integer not null, "created_at" datetime, "updated_at" datetime, "route" varchar, "parameters" text);
CREATE TABLE IF NOT EXISTS "roles" ("id" integer primary key autoincrement not null, "name" varchar not null, "display_name" varchar not null, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "permission_role" ("permission_id" integer not null, "role_id" integer not null, foreign key("permission_id") references "permissions"("id") on delete cascade, foreign key("role_id") references "roles"("id") on delete cascade, primary key ("permission_id", "role_id"));
CREATE TABLE IF NOT EXISTS "translations" ("id" integer primary key autoincrement not null, "table_name" varchar not null, "column_name" varchar not null, "foreign_key" integer not null, "locale" varchar not null, "value" text not null, "created_at" datetime, "updated_at" datetime);
CREATE TABLE permissions (id INT PRIMARY KEY AUTO_INCREMENT AUTO_INCREMENT NOT NULL, "key" VARCHAR(255) NOT NULL, table_name VARCHAR(255) DEFAULT NULL, created_at DATETIME DEFAULT NULL, updated_at DATETIME DEFAULT NULL);
CREATE TABLE users (id INT PRIMARY KEY AUTO_INCREMENT AUTO_INCREMENT NOT NULL, name VARCHAR(255) NOT NULL, email VARCHAR(255) NOT NULL, email_verified_at DATETIME DEFAULT NULL, password VARCHAR(255) NOT NULL, remember_token VARCHAR(255) DEFAULT NULL, created_at DATETIME DEFAULT NULL, updated_at DATETIME DEFAULT NULL, avatar VARCHAR(255) DEFAULT 'users/default.png', role_id BIGINT UNSIGNED DEFAULT NULL, "settings" text);
CREATE TABLE IF NOT EXISTS "user_roles" ("user_id" integer not null, "role_id" integer not null, foreign key("user_id") references "users"("id") on delete cascade, foreign key("role_id") references "roles"("id") on delete cascade, primary key ("user_id", "role_id"));
CREATE TABLE settings (id INT PRIMARY KEY AUTO_INCREMENT AUTO_INCREMENT NOT NULL, "key" VARCHAR(255) NOT NULL, display_name VARCHAR(255) NOT NULL, value CLOB DEFAULT NULL, details CLOB DEFAULT NULL, type VARCHAR(255) NOT NULL, "order" INTEGER DEFAULT 1 NOT NULL, "group" VARCHAR(255) DEFAULT NULL);
CREATE TABLE IF NOT EXISTS "pages" ("id" integer primary key autoincrement not null, "author_id" integer not null, "title" varchar not null, "excerpt" text, "body" text, "image" varchar, "slug" varchar not null, "meta_description" text, "meta_keywords" text, "status" varchar check ("status" in ('ACTIVE', 'INACTIVE')) not null default 'INACTIVE', "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "categories" ("id" integer primary key autoincrement not null, "parent_id" integer, "order" integer not null default '1', "name" varchar not null, "slug" varchar not null, "created_at" datetime, "updated_at" datetime, foreign key("parent_id") references "categories"("id") on delete set null on update cascade);
CREATE TABLE posts (id INT PRIMARY KEY AUTO_INCREMENT AUTO_INCREMENT NOT NULL, author_id INTEGER NOT NULL, category_id INTEGER DEFAULT NULL, title VARCHAR(255) NOT NULL, seo_title VARCHAR(255) DEFAULT NULL, excerpt CLOB DEFAULT NULL, body CLOB NOT NULL, image VARCHAR(255) DEFAULT NULL, slug VARCHAR(255) NOT NULL, meta_description CLOB DEFAULT NULL, meta_keywords CLOB DEFAULT NULL, status VARCHAR(255) DEFAULT 'DRAFT' NOT NULL, featured BOOLEAN DEFAULT 0 NOT NULL, created_at DATETIME DEFAULT NULL, updated_at DATETIME DEFAULT NULL);
CREATE TABLE Exam_Lis (id INT PRIMARY KEY AUTO_INCREMENT AUTO_INCREMENT NOT NULL, Soal text DEFAULT NULL, q1 text DEFAULT NULL, q2 text DEFAULT NULL, q3 text DEFAULT NULL, q4 text DEFAULT NULL, anw INTEGER DEFAULT NULL, grub INTEGER DEFAULT NULL, created_at text DEFAULT NULL, updated_at text DEFAULT NULL);
CREATE TABLE Exam_Plotings (id INT PRIMARY KEY AUTO_INCREMENT AUTO_INCREMENT NOT NULL, user_id INTEGER DEFAULT NULL, grub_exam INTEGER DEFAULT NULL, listofAnswer text DEFAULT NULL, score INTEGER DEFAULT NULL, Rivew text DEFAULT NULL, created_at text DEFAULT NULL, updated_at text DEFAULT NULL);
CREATE TABLE Game_Colaborators (id INT PRIMARY KEY AUTO_INCREMENT AUTO_INCREMENT NOT NULL, game_sectoryzer text DEFAULT NULL, created_at text DEFAULT NULL, updated_at text DEFAULT NULL);
CREATE TABLE Materis (id INT PRIMARY KEY AUTO_INCREMENT AUTO_INCREMENT NOT NULL, judul text DEFAULT NULL, simply text DEFAULT NULL, poster INTEGER DEFAULT NULL, isi text DEFAULT NULL, created_at text DEFAULT NULL, updated_at text DEFAULT NULL);
DELETE FROM sqlite_sequence;
CREATE UNIQUE INDEX "failed_jobs_uuid_unique" on "failed_jobs" ("uuid");
CREATE INDEX "personal_access_tokens_tokenable_type_tokenable_id_index" on "personal_access_tokens" ("tokenable_type", "tokenable_id");
CREATE UNIQUE INDEX "personal_access_tokens_token_unique" on "personal_access_tokens" ("token");
CREATE UNIQUE INDEX "data_types_name_unique" on "data_types" ("name");
CREATE UNIQUE INDEX "data_types_slug_unique" on "data_types" ("slug");
CREATE UNIQUE INDEX "menus_name_unique" on "menus" ("name");
CREATE UNIQUE INDEX "roles_name_unique" on "roles" ("name");
CREATE INDEX "permission_role_permission_id_index" on "permission_role" ("permission_id");
CREATE INDEX "permission_role_role_id_index" on "permission_role" ("role_id");
CREATE UNIQUE INDEX "translations_table_name_column_name_foreign_key_locale_unique" on "translations" ("table_name", "column_name", "foreign_key", "locale");
CREATE INDEX permissions_key_index ON permissions ("key");
CREATE UNIQUE INDEX users_email_unique ON users (email);
CREATE INDEX "user_roles_user_id_index" on "user_roles" ("user_id");
CREATE INDEX "user_roles_role_id_index" on "user_roles" ("role_id");
CREATE UNIQUE INDEX settings_key_unique ON settings ("key");
CREATE UNIQUE INDEX "pages_slug_unique" on "pages" ("slug");
CREATE UNIQUE INDEX "categories_slug_unique" on "categories" ("slug");
CREATE UNIQUE INDEX posts_slug_unique ON posts (slug);
COMMIT;
